import React, { useEffect, useState } from "react";
import Balance from "../../components/balance/Balance";
import Greetings from "../../components/greetings/Greetings";
import Header from "../../components/header/Header";
import { BtnSquare, ContainerBox } from "../../styled-components/GlobalStyles";
import { useTransactionStore } from "../../store";
import { Link, useNavigate } from "react-router-dom";
import { useStorePromo } from "../../store-promo";

function Game() {
  const transactionState = useTransactionStore((state) => state);
  const userProfileState = useTransactionStore((state) => state);
  const userPromoResponse = useTransactionStore((state) => state);

  userPromoResponse.userPromoResponse.promo.name = "";
  useEffect(() => {
    userProfileState.fetchUserProfiles();
  }, []);

  const navigate = useNavigate();
  const { setFetchPromo, promoDatas, filterPromo, dataFilter, loading } =
    useStorePromo();

  useEffect(() => {
    setFetchPromo();
    // const random = dataFilter[Math.floor(Math.random() * dataFilter.length)];
    // console.log(random);
  }, []);

  const [active, setActive] = useState(false);

  const handleClick = (e: React.MouseEvent<HTMLElement>) => {
    e.preventDefault();
    setActive(!active);
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    const EPromoID = document.getElementById("promo_id") as HTMLInputElement;

    transactionState.postUserPromo({
      user_id: userProfileState.userProfiles.id,
      promo_id: parseInt(EPromoID.value),
    });
  };

  useEffect(() => {
    console.log(transactionState.message, "2e21ui2gi12eu1ieg12eugeuig2u");
    if (userProfileState.message === "OK") {
      setFetchPromo();
    }
  }, [transactionState.message]);
  // console.log(dataFilter);
  let random = Math.floor(Math.random() * dataFilter.length);
  const [selectedBtn, setSelectedBtn] = useState(random);
  // console.log(random, dataFilter[random]);
  // const random = dataFilter[Math.floor(Math.random() * dataFilter.length)];
  // if (random == undefined)

  // console.log(random, "=====================");
  // var clicks = 0;

  // function onClick() {
  //   clicks += 1;
  //   document.getElementById("clicks").innerHTML as HTMLButtonElement = clicks;
  // }
  const [counter, setCounter] = useState(1);
  let decrementCounter = () => setCounter(counter - 1);
  if (counter <= 0) {
    decrementCounter = () => setCounter(1);
  }

  return (
    <div className="container">
      <Header />
      <Link to="/shipping">
        <button className="btn btn-success">Back</button>
      </Link>
      <div className="container">
        <h1 className="fw-bold d-flex justify-content-center">Gacha Game</h1>
        <p className="d-flex justify-content-center">
          Click on this box below to get a random reward!
        </p>

        <p className="d-flex justify-content-center">Chance: {counter}</p>
      </div>

      <ContainerBox>
        <div className="container text-center">
          <div className="row mt-5">
            {/* <button
              onClick={(e) => {
                handleSubmit(e);
                setSelectedBtn(dataFilter[random].id);
              }}
              id="promo_id"
              type="button"
              value={dataFilter[random].id}
              className="rounded-pill d-flex justify-content-center"
            >
              <BtnSquare>
                <h3>
                  {dataFilter[random].id == selectedBtn
                    ? dataFilter[random].name
                    : ""}
                </h3>
              </BtnSquare>
            </button> */}

            {/* <div className="col">
              <button
                key={random.id}
                onClick={() => setSelectedBtn(random.id)}
                className={
                  random.id == selectedBtn
                    ? "btn btn-light btn-lg btn-square-md"
                    : "btn btn-secondary btn-lg btn-square-md"
                }
                id="promo_id"
                type="button"
                value={random.id}
                // onClick={(e) => handleSubmit(e)}
              >
                <BtnSquare>
                  <h3 id="inputAmount">
                    {random.id == selectedBtn ? random.name : ""}
                  </h3>
                </BtnSquare>
              </button>
            </div> */}
            {dataFilter.map((promoState) => (
              <div className="col">
                {counter == 0 ? (
                  <button
                    key={promoState.id}
                    onClick={(e) => {
                      setSelectedBtn(promoState.id);
                      handleSubmit(e);
                      decrementCounter();
                    }}
                    className={
                      promoState.id == selectedBtn
                        ? "btn btn-danger btn-lg btn-square-md rounded-circle"
                        : "btn btn-warning btn-lg btn-square-md rounded-circle"
                    }
                    id="promo_id"
                    type="button"
                    value={dataFilter[random].id}
                    // onClick={(e) => handleSubmit(e)}
                    disabled
                  >
                    <BtnSquare>
                      {promoState.id == selectedBtn ? (
                        <h3 className="p-4 mt-4 text-white text-wrap fs-4">
                          {dataFilter[random].name}
                          {/* {userPromoResponse.userPromoResponse.promo.name} */}
                        </h3>
                      ) : (
                        ""
                      )}
                    </BtnSquare>
                  </button>
                ) : (
                  <button
                    key={promoState.id}
                    onClick={(e) => {
                      setSelectedBtn(promoState.id);
                      handleSubmit(e);
                      decrementCounter();
                    }}
                    className={
                      promoState.id == selectedBtn
                        ? "btn btn-danger btn-lg btn-square-md rounded-circle"
                        : "btn btn-warning btn-lg btn-square-md rounded-circle"
                    }
                    id="promo_id"
                    type="button"
                    value={dataFilter[random].id}
                    // onClick={(e) => handleSubmit(e)}
                  >
                    <BtnSquare>
                      {promoState.id == selectedBtn ? (
                        <h3 className="p-4 mt-4 text-white text-wrap fs-4">
                          {/* {dataFilter[random].name} */}
                          {userPromoResponse.userPromoResponse.status}
                        </h3>
                      ) : (
                        ""
                      )}
                    </BtnSquare>
                  </button>
                )}
              </div>
            ))}
            {/* {dataFilter.map(() => (
              <div className="col">
                <button
                  key={dataFilter[random].id}
                  onClick={(e) => {
                    handleSubmit(e);
                    setSelectedBtn(dataFilter[random].id);
                  }}
                  className={
                    dataFilter[random].id == selectedBtn
                      ? "btn btn-light btn-lg btn-square-md"
                      : "btn btn-secondary btn-lg btn-square-md"
                  }
                  id="promo_id"
                  type="button"
                  value={dataFilter[random].id}
                  // onClick={(e) => handleSubmit(e)}
                >
                  <BtnSquare>
                    <h3 id="inputAmount">
                      {dataFilter[random].id == selectedBtn
                        ? dataFilter[random].name
                        : ""}
                    </h3>
                  </BtnSquare>
                </button>
              </div>
            ))} */}
          </div>
        </div>
      </ContainerBox>
    </div>
  );
}

export default Game;
