import React, { useEffect, useState } from "react";
import Balance from "../../components/balance/Balance";
import Greetings from "../../components/greetings/Greetings";
import Header from "../../components/header/Header";
import { BtnSquare, ContainerBox } from "../../styled-components/GlobalStyles";
import { useTransactionStore } from "../../store";
import { Link, useNavigate } from "react-router-dom";
import { useStorePromo } from "../../store-promo";

function Game() {
  const transactionState = useTransactionStore((state) => state);
  const navigate = useNavigate();
  const { setFetchPromo, promoDatas, filterPromo, dataFilter, loading } =
    useStorePromo();

  useEffect(() => {
    setFetchPromo();
  }, []);

  const [active, setActive] = useState(false);

  const handleClick = (e: React.MouseEvent<HTMLElement>) => {
    e.preventDefault();
    setActive(!active);
  };

  const handleClick2 = (e: React.MouseEvent<HTMLElement>) => {
    e.preventDefault();
    setActive(!active);
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    const EInputAmount = document.getElementById(
      "inputAmount"
    ) as HTMLInputElement;

    transactionState.postUserTopUpTransactions({
      amount: parseInt(EInputAmount.value),
      source_of_fund_id: 3,
    });
  };

  return (
    <div className="container">
      <Header />
      <Link to="/shipping">
        <button className="btn btn-success">Back</button>
      </Link>
      <div className="container">
        <h1 className="fw-bold d-flex justify-content-center">Gatcha Game</h1>
        <p className="d-flex justify-content-center">
          Choose random box below to get reward!
        </p>
      </div>
      <ContainerBox>
        <div className="container text-center">
          <div className="row mt-5">
            {dataFilter.map((promoState) => (
              <div className="col">
                <button
                  onClick={active ? handleClick2 || handleSubmit : handleClick}
                  className={
                    active
                      ? "btn btn-light btn-lg btn-square-md"
                      : "btn btn-secondary btn-lg btn-square-md"
                  }
                  type="button"
                >
                  <BtnSquare>
                    <h3 id="inputAmount">{active ? 20000 : ""}</h3>
                  </BtnSquare>
                </button>
              </div>
            ))}

            {/* <div className="col">
              <button
                onClick={active ? handleClick2 : handleClick}
                className={
                  active
                    ? "btn btn-light btn-lg btn-square-md"
                    : "btn btn-secondary btn-lg btn-square-md"
                }
                type="button"
              >
                <BtnSquare>
                  <h3>{active ? 50000 : ""}</h3>
                </BtnSquare>
              </button>
            </div>
            <div className="col">
              <button
                onClick={active ? handleClick2 : handleClick}
                className={
                  active
                    ? "btn btn-light btn-lg btn-square-md"
                    : "btn btn-secondary btn-lg btn-square-md"
                }
                type="button"
              >
                <BtnSquare>
                  <h3>{active ? 100000 : ""}</h3>
                </BtnSquare>
              </button>
            </div> */}
          </div>
        </div>
      </ContainerBox>
    </div>
  );
}

export default Game;
