import React, { FormEvent, useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";
import Header from "../../components/header/Header";
import TableShipping from "../../components/table/TableShipping";
import { useTransactionStore } from "../../store";
import {
  Container,
  ContainerForm,
  IconGreen,
} from "../../styled-components/GlobalStyles";
import ProfileImage from "../../assets/profile.png";
import { useStoreAddress } from "../../store-address";
import useFetch from "../../hooks/useFetch";
import { IUpdateProfile } from "../../interface";

function EditProfile() {
  const { id } = useParams();
  const { data, loading, error } = useFetch<IUpdateProfile>(
    `http://localhost:8080/api/user/profile/${id}`
  );
  const transactionState = useTransactionStore((state) => state);
  const userProfileState = useTransactionStore((state) => state);
  const { setFetchAddress, addressDatas, dataFilter } = useStoreAddress();

  console.log(addressDatas[0].full_address, "===================");
  console.log(dataFilter, "1111111111111111111111");
  useEffect(() => {
    transactionState.fetchUserTransactions();
    userProfileState.fetchUserProfiles();
  }, []);
  useEffect(() => {
    setFetchAddress();
  }, []);

  const [input, setInput] = useState({
    name: "",
    email: "",
    phone_number: 0,
  });

  const handleChange = (event: React.ChangeEvent<HTMLSelectElement>) => {};

  const handleChangeInput = (event: FormEvent<HTMLInputElement>) => {
    console.log(event.currentTarget);
    setInput({
      ...input,
      [event.currentTarget.name]: event.currentTarget.value,
    });
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    const EInputName = document.getElementById("inputName") as HTMLInputElement;
    const EInputEmail = document.getElementById(
      "inputEmail"
    ) as HTMLInputElement;
    const EInputPhoneNumber = document.getElementById(
      "inputPhoneNumber"
    ) as HTMLInputElement;

    transactionState.putUpdateProfile({
      id: 0,
      name: EInputFullAddress.value,
      email: EInputRecipientName.value,
      phone_number: parseInt(EInputRecipientPhoneNumber.value),
    });
  };

  function makeid(length: number) {
    let result = "";
    let characters = "ABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789";
    let charactersLength = characters.length;
    for (var i = 0; i < length; i++) {
      result += characters.charAt(Math.floor(Math.random() * charactersLength));
    }
    return result;
  }
  return (
    <div className="container">
      <Header />
      <Link to="/profile">
        <button className="btn btn-success">Back</button>
      </Link>
      <div className="container" style={{ marginTop: "5rem" }}>
        <div className="row justify-content-md-center">
          <div className="col xs lg-2">
            <div className="md-auto">
              <h2 className="d-flex justify-content-center fw-bold">
                Edit Profile
              </h2>
              <form className="p-2">
                <div className="mb-3">
                  <label>Name</label>
                  <input
                    className="form-control"
                    type="text"
                    name="name"
                    placeholder="Enter your name..."
                    id="inputName"
                    onChange={(event) => handleChangeInput(event)}
                  />
                </div>
                <div className="mb-3">
                  <label>Email</label>
                  <input
                    className="form-control"
                    type="text"
                    name="email"
                    placeholder="Enter your email..."
                    id="inputEmail"
                    onChange={(event) => handleChangeInput(event)}
                  />
                </div>
                <div className="mb-3">
                  <label>Phone Number</label>
                  <input
                    className="form-control"
                    type="number"
                    name="phone_number"
                    placeholder="Enter your phone number..."
                    id="inputPhoneNumber"
                    onChange={(event) => handleChangeInput(event)}
                  />
                </div>
                <div className="mb-3">
                  <label htmlFor="formFile" className="form-label">
                    Photo
                  </label>
                  <input className="form-control" type="file" id="formFile" />
                </div>
                <div className="p-2 d-grid gap-3">
                  <button
                    className="btn btn-success"
                    type="submit"
                    data-bs-toggle="modal"
                    data-bs-target="#exampleModal"
                    onClick={(e) => handleSubmit(e)}
                  >
                    Edit
                  </button>
                </div>
              </form>
            </div>
          </div>
          <div className="col xs lg-2 d-flex justify-content-end">
            <img src={ProfileImage} height="400px" alt="Address Image" />
          </div>
        </div>
      </div>
      <div className="container">
        <div className="row justify-content-md-center">
          <div className="col"></div>
          <ContainerForm>
            <div className="md-2"></div>
          </ContainerForm>
          <div
            className="modal fade"
            id="exampleModal"
            tabIndex={-1}
            aria-labelledby="exampleModalLabel"
            aria-hidden="true"
          >
            <div className="modal-dialog">
              <div className="modal-content">
                <div className="modal-header d-flex justify-content-center">
                  <Container>
                    <div className="d-flex justify-content-center flex-column mb-3">
                      <div className="p-2">
                        <IconGreen>
                          <i className="bi bi-check-circle-fill"></i>
                        </IconGreen>
                      </div>
                      <div className="p-2">
                        <h1
                          className="modal-title fs-5 d-flex justify-content-center text-center fw-bold"
                          id="exampleModalLabel"
                        >
                          Shipping Success
                        </h1>
                      </div>
                    </div>
                  </Container>
                </div>
                <div className="modal-body d-flex justify-content-center">
                  <div className="row">
                    <div className="col">
                      <p>Full Address</p>
                      <p>Recipient Name</p>
                      <p>Recipient Phone Number</p>
                    </div>
                    <div className="col">
                      <p>{addressDatas[0].full_address}</p>
                      <p>{addressDatas[0].recipient_name}</p>
                      <p>{addressDatas[0].recipient_phone_number}</p>
                    </div>
                  </div>
                </div>
                <div className="modal-footer d-flex justify-content-center">
                  <button type="button" className="btn btn-primary">
                    Print
                  </button>
                  <Link to="/address">
                    <button
                      type="button"
                      className="btn btn-secondary"
                      data-bs-dismiss="modal"
                    >
                      Close
                    </button>
                  </Link>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default EditProfile;
