import React, { FormEvent, useEffect, useState } from "react";
import { Container } from "react-bootstrap";
import { Link, useNavigate, useParams } from "react-router-dom";
import Header from "../../components/header/Header";
import useFetch from "../../hooks/useFetch";
import { IShippingResponse } from "../../interface";
import { useTransactionStore } from "../../store";
import { useStoreProfile } from "../../store-shipping";
import { ContainerForm, IconGreen } from "../../styled-components/GlobalStyles";
import ShippingImage from "../../assets/payment.png";
import Balance from "../../components/balance/Balance";
import { useStorePromo } from "../../store-user-promo";

function Payment() {
  const navigate = useNavigate();
  const { id } = useParams();
  const { data, loading, error } = useFetch<IShippingResponse>(
    `http://localhost:8080/api/user/shipping/${id}`
  );

  console.log("data listCustom", data);
  console.log(data?.data.size.price, "=========================");

  const transactionState = useTransactionStore((state) => state);
  const userProfileState = useTransactionStore((state) => state);
  const { setFetchShipping, shippingDatas, dataFilter } = useStoreProfile();
  const { setFetchUserPromo, userPromoDatas } = useStorePromo();

  //   console.log(shippingDatas[0].add_on.name, "===================");
  console.log(dataFilter, "1111111111111111111111");
  useEffect(() => {
    transactionState.fetchUserTransactions();
    userProfileState.fetchUserProfiles();
  }, []);
  useEffect(() => {
    setFetchShipping();
    setFetchUserPromo();
  }, []);

  const [input, setInput] = useState({
    size_id: 0,
    category_id: 0,
    add_on_id: 0,
    address_id: 0,
  });

  const handleChange = (event: React.ChangeEvent<HTMLSelectElement>) => {};

  const handleChangeInput = (event: FormEvent<HTMLInputElement>) => {
    console.log(event.currentTarget);
    setInput({
      ...input,
      [event.currentTarget.name]: event.currentTarget.value,
    });
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    const EShippingId = document.getElementById(
      "shipping_id"
    ) as HTMLSelectElement;
    const EPromoId = document.getElementById("promo_id") as HTMLSelectElement;

    transactionState.postCreatePayment({
      shipping_id: parseInt(EShippingId.value),
      promo_id: parseInt(EPromoId.value),
    });
  };
  return (
    <div className="container">
      <Header />
      <button
        onClick={() => {
          navigate(`/shipping/${id}`, {
            replace: true,
          });
        }}
        className="btn btn-success"
      >
        Back
      </button>
      <Balance />
      {error ? <p>Error</p> : null}
      {loading ? (
        <p>Loading</p>
      ) : (
        <div>
          <div className="container" style={{ marginTop: "5rem" }}>
            <div className="row justify-content-md-center">
              <div className="col xs lg-2">
                <div className="md-auto">
                  <h2 className="d-flex justify-content-center fw-bold">
                    Payment
                  </h2>
                  <form className="p-2">
                    <div className="mb-3">
                      <label>Shipping ID</label>
                      <select
                        onChange={(e) => handleChange(e)}
                        defaultValue={id}
                        className="form-select"
                        id="shipping_id"
                        name="shipping_id"
                        disabled
                      >
                        <option value={id}>{id}</option>
                      </select>
                    </div>
                    <div className="mb-3">
                      <label>Choose Promo</label>
                      <select
                        onChange={(e) => handleChange(e)}
                        defaultValue="0"
                        className="form-select"
                        id="promo_id"
                        name="promo_id"
                      >
                        <option value="0">No Promo</option>
                        {userPromoDatas.map((a) => (
                          <option value={a.id}>{a.promo_id.name}</option>
                        ))}
                        {/* <option value="1">40% Discount Vouchers</option>
                        <option value="2">60% Discount Vouchers</option>
                        <option value="3">60% Discount Vouchers</option> */}
                      </select>
                    </div>
                    <div className="p-2 d-grid gap-3">
                      <button
                        className="btn btn-success"
                        type="submit"
                        data-bs-toggle="modal"
                        data-bs-target="#exampleModal"
                        onClick={(e) => handleSubmit(e)}
                      >
                        Pay
                      </button>
                    </div>
                  </form>
                </div>
              </div>
              <div className="col xs lg-2 d-flex justify-content-end">
                <img src={ShippingImage} height="400px" alt="Shipping Image" />
              </div>
            </div>
          </div>
          <div className="container">
            <div className="row justify-content-md-center">
              <div className="col"></div>
              <ContainerForm>
                <div className="md-2"></div>
              </ContainerForm>
              <div
                className="modal fade"
                id="exampleModal"
                tabIndex={-1}
                aria-labelledby="exampleModalLabel"
                aria-hidden="true"
              >
                <div className="modal-dialog">
                  <div className="modal-content">
                    <div className="modal-header d-flex justify-content-center">
                      <Container>
                        <div className="d-flex justify-content-center flex-column mb-3">
                          <div className="p-2">
                            <IconGreen>
                              <i className="bi bi-check-circle-fill"></i>
                            </IconGreen>
                          </div>
                          <div className="p-2">
                            <h1
                              className="modal-title fs-5 d-flex justify-content-center text-center fw-bold"
                              id="exampleModalLabel"
                            >
                              Payment Success
                            </h1>
                          </div>
                        </div>
                      </Container>
                    </div>
                    <div className="modal-body d-flex justify-content-center"></div>
                    <div className="modal-footer d-flex justify-content-center">
                      <Link to="/shipping">
                        <button
                          type="button"
                          className="btn btn-secondary"
                          data-bs-dismiss="modal"
                        >
                          Close
                        </button>
                      </Link>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default Payment;
