import Greetings from "../../components/greetings/Greetings";
import Header from "../../components/header/Header";
import { BtnSquare, ContainerBox } from "../../styled-components/GlobalStyles";
import { useTransactionStore } from "../../store";
import { Link, useNavigate } from "react-router-dom";
import { useStorePromo } from "../../store-promo";

function Game() {
  const transactionState = useTransactionStore((state) => state);
  const userProfileState = useTransactionStore((state) => state);

  useEffect(() => {
    userProfileState.fetchUserProfiles();
  }, []);

  const navigate = useNavigate();
  const { setFetchPromo, promoDatas, filterPromo, dataFilter, loading } =
    useStorePromo();
  const random = dataFilter[Math.floor(Math.random() * dataFilter.length)];
  const [selectedBtn, setSelectedBtn] = useState(0);
  useEffect(() => {
    setFetchPromo();
  }, []);

  const [active, setActive] = useState(false);

  const handleClick = (e: React.MouseEvent<HTMLElement>) => {
    e.preventDefault();
    setActive(!active);
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    const EPromoID = document.getElementById("promo_id") as HTMLInputElement;

    transactionState.postUserPromo({
      user_id: 3,
      promo_id: parseInt(EPromoID.value),
    });
  };

  // console.log(dataFilter);
  // const random = Math.floor(Math.random() * dataFilter.length);
  // console.log(random, dataFilter[random]);

  // console.log(random);

  return (
    <div className="container">
      <Header />
      <Link to="/shipping">
        <button className="btn btn-success">Back</button>
      </Link>
      <div className="container">
        <h1 className="fw-bold d-flex justify-content-center">Gacha Game</h1>
        <p className="d-flex justify-content-center">
          Click on this box below to get a random reward!
        </p>
      </div>
      <ContainerBox>
        <div className="container text-center">
          <div className="row mt-5">
            <button
              // onClick={() => {
              //   setSelectedBtn(random.id);
              // }}
              onClick={(e) => {
                handleSubmit(e);
                setSelectedBtn(random.id);
              }}
              id="promo_id"
              type="button"
              value={random.id}
              className="rounded-pill"
            >
              <BtnSquare>
                <h3 id="inputAmount" className="d-flex justify-content-center">
                  {random.id == selectedBtn ? random.name : ""}
                </h3>
              </BtnSquare>
            </button>

            {/* <div className="col">
              <button
                key={random.id}
                onClick={() => setSelectedBtn(random.id)}
                className={
                  random.id == selectedBtn
                    ? "btn btn-light btn-lg btn-square-md"
                    : "btn btn-secondary btn-lg btn-square-md"
                }
                id="promo_id"
                type="button"
                value={random.id}
                // onClick={(e) => handleSubmit(e)}
              >
                <BtnSquare>
                  <h3 id="inputAmount">
                    {random.id == selectedBtn ? random.name : ""}
                  </h3>
                </BtnSquare>
              </button>
            </div> */}
            {/* {dataFilter.map((promoState) => (
              <div className="col">
                <button
                  key={promoState.id}
                  onClick={() => setSelectedBtn(promoState.id)}
                  className={
                    promoState.id == selectedBtn
                      ? "btn btn-light btn-lg btn-square-md"
                      : "btn btn-secondary btn-lg btn-square-md"
                  }
                  id="promo_id"
                  type="button"
                  value={promoState.id}
                  // onClick={(e) => handleSubmit(e)}
                >
                  <BtnSquare>
                    <h3 id="inputAmount">
                      {promoState.id == selectedBtn ? promoState.name : ""}
                    </h3>
                  </BtnSquare>
                </button>
              </div>
            ))} */}
