    <div className="container">
      <Header />
      <Link to="/shipping">
        <button className="btn btn-success">Back</button>
      </Link>
      <div className="container">
        <h1 className="fw-bold d-flex justify-content-center">Gacha Game</h1>
        <p className="d-flex justify-content-center">
          Click on this box below to get a random reward!
        </p>
      </div>

      <ContainerBox>
        <div className="container text-center">
          <div className="row mt-5">
            {/* <button
              onClick={(e) => {
                handleSubmit(e);
                setSelectedBtn(dataFilter[random].id);
              }}
              id="promo_id"
              type="button"
              value={dataFilter[random].id}
              className="rounded-pill d-flex justify-content-center"
            >
              <BtnSquare>
                <h3>
                  {dataFilter[random].id == selectedBtn
                    ? dataFilter[random].name
                    : ""}
                </h3>
              </BtnSquare>
            </button> */}

            {/* <div className="col">
              <button
                key={random.id}
                onClick={() => setSelectedBtn(random.id)}
                className={
                  random.id == selectedBtn
                    ? "btn btn-light btn-lg btn-square-md"
                    : "btn btn-secondary btn-lg btn-square-md"
                }
                id="promo_id"
                type="button"
                value={random.id}
                // onClick={(e) => handleSubmit(e)}
              >
                <BtnSquare>
                  <h3 id="inputAmount">
                    {random.id == selectedBtn ? random.name : ""}
                  </h3>
                </BtnSquare>
              </button>
            </div> */}
            {dataFilter.map((promoState) => (
              <div className="col">
                <button
                  key={promoState.id}
                  onClick={(e) => {
                    setSelectedBtn(promoState.id);
                    handleSubmit(e);
                  }}
                  className={
                    promoState.id == selectedBtn
                      ? "btn btn-light btn-lg btn-square-md"
                      : "btn btn-secondary btn-lg btn-square-md"
                  }
                  id="promo_id"
                  type="button"
                  value={dataFilter[random].id}
                  // onClick={(e) => handleSubmit(e)}
                >
                  <BtnSquare>
                    {promoState.id == selectedBtn ? (
                      <h3>{userPromoResponse.promo.name}</h3>
                    ) : (
                      ""
                    )}
                  </BtnSquare>
                </button>
              </div>
            ))}
            {/* {dataFilter.map(() => (
              <div className="col">
                <button
                  key={dataFilter[random].id}
                  onClick={(e) => {
                    handleSubmit(e);
                    setSelectedBtn(dataFilter[random].id);
                  }}
                  className={
                    dataFilter[random].id == selectedBtn
                      ? "btn btn-light btn-lg btn-square-md"
                      : "btn btn-secondary btn-lg btn-square-md"
                  }
                  id="promo_id"
                  type="button"
                  value={dataFilter[random].id}
                  // onClick={(e) => handleSubmit(e)}
                >
                  <BtnSquare>
                    <h3 id="inputAmount">
                      {dataFilter[random].id == selectedBtn
                        ? dataFilter[random].name
                        : ""}
                    </h3>
                  </BtnSquare>
                </button>
              </div>
            ))} */}
          </div>
        </div>
      </ContainerBox>
    </div>
  );
}

export default Game;
