                }
                id="promo_id"
                type="button"
                value={random.id}
                // onClick={(e) => handleSubmit(e)}
              >
                <BtnSquare>
                  <h3 id="inputAmount">
                    {random.id == selectedBtn ? random.name : ""}
                  </h3>
                </BtnSquare>
              </button>
            </div> */}
              {dataFilter.map((promoState) => (
                <div className="col">
                  {counter == 0 ? (
                    <button
                      key={promoState.id}
                      onClick={(e) => {
                        setSelectedBtn(promoState.id);
                        handleSubmit(e);
                        decrementCounter();
                      }}
                      className={
                        promoState.id == selectedBtn
                          ? "btn btn-danger btn-lg btn-square-md rounded-circle"
                          : "btn btn-warning btn-lg btn-square-md rounded-circle"
                      }
                      id="promo_id"
                      type="button"
                      value={dataFilter[random].id}
                      // onClick={(e) => handleSubmit(e)}
                      disabled
                    >
                      <BtnSquare>
                        {promoState.id == selectedBtn ? (
                          <h3 className="p-4 mt-4 text-white text-wrap fs-4">
                            {/* {dataFilter[random].name} */}
                            {userPromoResponse.promo.name}
                          </h3>
                        ) : (
                          ""
                        )}
                      </BtnSquare>
                    </button>
                  ) : (
                    <button
                      key={promoState.id}
                      onClick={(e) => {
                        setSelectedBtn(promoState.id);
                        handleSubmit(e);
                        decrementCounter();
                      }}
                      className={
                        promoState.id == selectedBtn
                          ? "btn btn-danger btn-lg btn-square-md rounded-circle"
                          : "btn btn-warning btn-lg btn-square-md rounded-circle"
                      }
                      id="promo_id"
                      type="button"
                      value={dataFilter[random].id}
                      // onClick={(e) => handleSubmit(e)}
                    >
                      <BtnSquare>
                        {promoState.id == selectedBtn ? (
                          <h3 className="p-4 mt-4 text-white text-wrap fs-4">
                            {/* {dataFilter[random].name} */}
                            {userPromoResponse.promo.name}
                          </h3>
                        ) : (
                          ""
                        )}
                      </BtnSquare>
                    </button>
                  )}
                </div>
              ))}
              {/* {dataFilter.map(() => (
              <div className="col">
                <button
                  key={dataFilter[random].id}
