import { Link, useNavigate } from "react-router-dom";
import { FormEvent, useState } from "react";
import axios from "axios";
import { ContainerForm } from "../../styled-components/GlobalStyles";
import HeaderLoginRegister from "../../components/header/HeaderLoginRegister";

function Register() {
  const navigate = useNavigate();

  const [input, setInput] = useState({
    name: "",
    password: "",
    email: "",
  });

  const handleChange = (event: FormEvent<HTMLInputElement>) => {
    console.log(event.currentTarget);
    setInput({
      ...input,
      [event.currentTarget.name]: event.currentTarget.value,
    });
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    try {
      console.log(input);
      let response = await axios.post(
        "http://localhost:8080/api/auth/register",
        {
          name: input.name,
          email: input.email,
          password: input.password,
        }
      );
      console.log(response);
      navigate("/login", { replace: true });
      localStorage.setItem("id_token", JSON.stringify(response.data.data));
    } catch (error) {
      alert(error);
    }
  };

  return (
    <div className="container register">
      <HeaderLoginRegister />
      <div className="container">
        <div className="row justify-content-md-center">
          <div className="col xs lg-2">
            <div className="md-2">
              <h2 className="d-flex justify-content-center fw-bold">
                Register
              </h2>
              <form onSubmit={(event) => handleSubmit(event)}>
                <div className="mb-3">
                  <label>Username</label>
                  <input
                    className="form-control"
                    type="text"
                    name="name"
                    placeholder="Enter your username..."
                    onChange={(event) => handleChange(event)}
                  />
                </div>
                <div className="mb-3">
                  <label>Password</label>
                  <input
                    className="form-control"
                    type="password"
                    name="password"
                    placeholder="Enter password..."
                    onChange={(event) => handleChange(event)}
                  />
                </div>
                <div className="mb-3">
                  <label>Email</label>
                  <input
                    className="form-control"
                    type="email"
                    name="email"
                    placeholder="Enter your email..."
                    onChange={(event) => handleChange(event)}
                  />
                </div>
                <div className="d-grid gap-2">
                  <button className="btn btn-primary" type="submit">
                    Register
                  </button>
                </div>
                <div className="mt-3 d-flex justify-content-center">
                  <p className="small-text">
                    Already have an account?{""}
                    <Link to="/login" className="text-black">
                      Login
                    </Link>
                  </p>
                </div>
              </form>
            </div>
          </div>
          <div className="col xs lg-2"></div>
        </div>
      </div>
    </div>
  );
}

export default Register;
